import React, { Component } from 'react';

import 'bootstrap/dist/css/bootstrap.css';
import PropTypes from 'prop-types';
import Greet from './components/Greet';

class App extends Component {
  state = { // mutable
    messaage: 'Default Message'
  }
  constructor(props) {
    super();
    console.log("App:: constructor()");

  }

  changeMessage(msg){
    this.setState({messaage:msg})
  }
  render() {

    let { title, trainer } = this.props;
    let {messaage}=this.state;

    console.log("App:: render()");
    return (
      <div className="container">
        <hr />
        <h1>{title} - by <span className="badge badge-info">{trainer}</span></h1>
        <hr />

        <button onClick={e=>this.changeMessage('Good Morning')} className="btn btn-primary" style={{margin:"5px"}}>GM</button>
        <button className="btn btn-primary" style={{margin:"5px"}}>GF</button>
        <button className="btn btn-primary" style={{margin:"5px"}}>GE</button>

        <Greet messaage={messaage} />
      </div>
    );
  }
}

App.defaultProps = {
  // title: 'react',
  trainer: 'Bucky Wall'
}


App.propTypes = {
  title: PropTypes.string.isRequired,
  trainer: PropTypes.string
}
export default App;
